mapper.select:
/* mb.pc.selectCurrentPassword */
	select LOGIN_PW
	from psmb0001
	where MBR_ID = #{MBR_ID};

mapper.update:
/* mb.pc.updatePassword */
	update psmb0001 
	set LOGIN_PW = #{newPassword}
	, UPD_DTM = TO_CHAR(SYSDATE(), 'YYYYMMDDHH24MISS')
	, UPD_ID = #{MBR_ID} 
	where MBR_ID = #{MBR_ID};
