mapper.select:
/* mb.sb.selectBlockList */
	select b.NICKNM, b.PF_IMG, b.DEF_IMG, a.BCK_TGT_ID
	from psbk0001 a, psmb0001 b 
	where a.BCKR_ID = #{MBR_ID} and
		a.BCK_TGT_ID = b.MBR_ID;

mapper.delete:
/* mb.sb.deleteBlock */
	delete from psbk0001
	where BCKR_ID = #{MBR_ID} and BCK_TGT_ID = #{BCK_TGT_ID};
